SELECT 
	seq
	,name
	,id
	,pass
	,adderss
	,tel
	,defaultNy
FROM
	memberShip
WHERE 1=1
		AND seq = #{seq} 
		AND id = #{id} 
	AND pass = #{pass}
	AND name = #{name}
	AND adderss = #{adderss}
	AND tel = #{tel}
	AND defaultNy = #{defaultNy}

mapper.update:
UPDATE memberShip
SET
	defaultNy = #{defaultNy}
	,name = #{name}
	,tel = #{tel}
	,adderss = #{adderss}
	,id = #{id}
	,pass = #{pass}
WHERE
	seq= #{seq}


UPDATE memberEmail
SET
	email = #{email}
WHERE
	memberShip_seq = #{seq}

mapper.insert:
