mapper.select:
	SELECT aa.* FROM (   	
   		SELECT


	) aa
	LIMIT #{rowNumToShow} OFFSET #{startRnumForMysql}

SELECT 
	seq
  		,defaultNy
  		,category
  		,productName
  		,productNumber
  		,productPrice
  		,productStock
  		,productDesc
  		,productDate
  		,productHits
  		,favorites
  		,sale
  		,memberMembership_seq
FROM
	product
WHERE 1=1
		AND seq = #{seq} 
	AND defaultNy = #{defaultNy}
		AND category = #{category} 
	AND productName = #{productName}
	AND productNumber = #{productNumber}
	AND productPrice = #{productPrice}
	AND productStock = #{productStock}
	AND productDesc = #{productDesc}
	AND productDate = #{productDate}
	AND productHits = #{productHits}
	AND favorites = #{favorites}
	AND sale = #{sale}
	AND memberMembership_seq = #{memberMembership_seq}

mapper.update:
UPDATE product
SET
		defaultNy = #{defaultNy}
  		,category = #{category}
  		,productName = #{productName}
  		,productNumber = #{productNumber}
  		,productPrice = #{productPrice}
  		,productStock = #{productStock}
  		,productDesc = #{productDesc}
  		,productDate = #{productDate}
  		,productHits = #{productHits}
  		,favorites = #{favorites}
  		,sale = #{sale}
  		,memberMembership_seq = #{memberMembership_seq}
WHERE
	seq= #{seq}

mapper.insert:
insert into product (
	defaultNy
	,category
	,productName
	,productNumber
	,productPrice
	,productStock
	,productDesc
	,productDate
	,productHits
	,favorites
	,sale
	,memberMembership_seq
) values (
	1
	,#{category}
	,#{productName}
	,#{productNumber}
	,#{productPrice}
	,#{productStock}
	,#{productDesc}
	,#{productDate}
	,#{productHits}
	,#{favorites}
	,#{sale}
	,#{memberMembership_seq}
)

		SELECT last_insert_id()
